
exports.validatePager = function(options) {

  var page = isFinite(options.page) ? parseInt(options.page) : 0;
  var pageSize = isFinite(options.pageSize) ? parseInt(options.pageSize) : 10;

  if (page < 0) {
    throw mkError('EINVALIDARG', 'Page must be greater or equals zero');
  }

  if (pageSize <= 0) {
    throw mkError('EINVALIDARG', 'Page size must be greater than zero');
  }

  options.page = page;
  options.pageSize = pageSize;

  return options;
}

exports.page = function(arr, pager) {
  return arr.slice(pager.page * pager.pageSize, pager.pageSize);
}